;		this.checkStatus(locResult);		// On ne check pas quand il s'agit du HTML car renvoie une liste		this.checkParameter(locResult, "email", this.MAIL);	}	@Test(timeout = 3000L, expected = IllegalArgumentException.class)	public void testInfosIllegalArgumentException() {		_service.infos(this._type, null);	}	@Test(timeout = 10000L)	public void testList_EnumReturnType() {		String locResult = _service.list(this._type);		// On considère que le status vérifie la validité du test, afin de ne pas le		// rendre dépendant d'une liste de contacts		this.checkStatus(locResult);	}	@Test(timeout = 10000L)	public void testList_EnumReturnType_EnumMap() {		Map<String, String> locParameters = new HashMap<String, String>(2);		locParameters.put("limit", "1");		locParameters.put("blocked", "1");		String locResult = _service.list(this._type, locParameters);		// On considère que le status vérifie la validité du test, afin de ne pas le		// rendre dépendant d'une liste de contacts		this.checkStatus(locResult);	}	@Test(timeout = 10000L)	public void testOpeners_EnumReturnType() {		String locResult = _service.openers(this._type);		// On considère que le status vérifie la validité du test, afin de ne pas le		// rendre dépendant d'une liste de contacts		this.checkStatus(locResult);	}	@Test(timeout = 10000L)	public void testOpeners_EnumReturnType_Long() {		String locResult = _service.openers(this._type, null);		// On considère que le status vérifie la validité du test, afin de ne pas le		// rendre dépendant d'une liste de contacts		this.checkStatus(locResult);	}	@Test(timeout = 10000L)	public void testOpeners_3args() {		String locResult = _service.openers(this._type, null, Integer.valueOf(1));		this.checkStatus(locResult);		this.checkParameter(locResult, "total_cnt", "1");	}	@Test(timeout = 10000L)	public void testOpeners_4args() {		String locResult = _service.openers(this._type, null, Integer.valueOf(1), Integer.valueOf(0));		this.checkStatus(locResult);		this.checkParameter(locResult, "total_cnt", "1");	}}